import React from "react";
import "./ProjectDetailSide.css";
import parse from "html-react-parser";
import { NO_DATA_AVALAIBLE } from "helpers/constants";
import urlImageAdapter from "helpers/urlImageAdapter";

const ProjectDetailSide = ({ project }) => {
  return (
    <>
      {project && (
        <div className="row">
          <div className="col-12 col-md-8">
            <p className="project-details text-justify">
              {parse(String(project?.description ? project?.description : ""))}
            </p>
            {project.image_url && (
              <img
                className="footer-img"
                alt="profile"
                src={urlImageAdapter(project.image_url)}
              />
            )}

            <p className="project-details text-justify">
              {parse(String(project?.objectives ? project?.objectives : ""))}
            </p>
          </div>
          <div className="col-12 col-md-3 ml-md-5 ">
            <div className="side-card">
              <div className="col-12 ">
                <h1
                  className="section-title mb-5"
                  style={{
                    color: "#3f51b5",
                    fontSize: "23.9888px",
                    lineHeight: "32.0011px",
                    fontWeight: "bold",
                  }}
                >
                  Budget
                </h1>
                <ul className="list-unstyled">
                  <li>
                    <img
                      style={{ width: "34px" }}
                      alt="yellow icon"
                      src="/assets/icons/yellow.svg"
                    />{" "}
                    <span className="ml-4">
                      {" "}
                      {project.total_cost
                        ? project.total_cost
                        : NO_DATA_AVALAIBLE}
                    </span>
                  </li>
                </ul>
              </div>

              <h1
                style={{
                  color: "#3f51b5",
                  fontSize: "23.9888px",
                  lineHeight: "32.0011px",
                  fontWeight: "bold",
                }}
                className="bold-title"
              >
                Budget
              </h1>
              <h4 className="secondary-title">
                {project.total_cost ? project.total_cost : NO_DATA_AVALAIBLE}
              </h4>

              <h1 className="bold-title">Status</h1>
              <h4 className="secondary-title">
                {project?.status?.name
                  ? project?.status?.name
                  : NO_DATA_AVALAIBLE}
              </h4>

              <h1 className="bold-title">Actual Start</h1>

              <h4 className="secondary-title">
                {project?.actual_start
                  ? project?.actual_end?.split("T")[0]
                  : NO_DATA_AVALAIBLE}
              </h4>
              <h1 className="bold-title">Actual end</h1>
              <h4 className="secondary-title">
                {" "}
                {project?.actual_end
                  ? project?.actual_end?.split("T")[0]
                  : NO_DATA_AVALAIBLE}
              </h4>
              <h1 className="bold-title">Country</h1>
              <h4 className="secondary-title">
                {project?.country?.name
                  ? project?.country?.name
                  : NO_DATA_AVALAIBLE}
              </h4>
            </div>
          </div>{" "}
        </div>
      )}
    </>
  );
};

export default ProjectDetailSide;
