import React, { useState, useEffect } from "react";
import CreateIcon from "@material-ui/icons/Create";
import InputOption from "../inputOption/InputOption";
import Post from "../post/Post";
import ImageIcon from "@material-ui/icons/Image";
import SubscriptionsIcon from "@material-ui/icons/Subscriptions";
import EventNoteIcon from "@material-ui/icons/EventNote";
import CalendarViewDayIcon from "@material-ui/icons/CalendarViewDay";
//import { db } from "./firebase.js";
//import firebase from "firebase";
import { useSelector } from "react-redux";
//import { selectUser } from "./features/userSlice";
import FlipMove from "react-flip-move";
import "./Feed.css";
import HeaderOption from "../header/HeaderOption";

function Feed() {
  // const user = useSelector(selectUser);
  const [input, setInput] = useState("");
  const posts = Array(1000).fill(
    <Post
      key={1}
      name={"Oussama Basry"}
      description={"Full Stack Web Developper"}
      message={
        "Je suis heureux de partager avec vous mon nouveau projet, j'ai développé un portfolio dynamique avec une dashboard admin dans laquelle vous pouvez afficher, ajouter, modifier et supprimer les éléments de toutes les sections d'un portfolio: formations, expériences, compétences, projets et contact."
      }
      photoUrl={
        "https://media-exp1.licdn.com/dms/image/C4D03AQGDiWuWWd12MA/profile-displayphoto-shrink_200_200/0/1617837846595?e=1635379200&v=beta&t=mrhHf9x3j2LoDQEwjJeDs0wzl7iOqeVWioy-vbzCxPg"
      }
    />
  );

  useEffect(() => {}, []);

  const sendPost = (e) => {};

  return (
    <div className="feed">
      <div className="feed__inputContainer">
        <div className="feed__input">
          <HeaderOption avatar={true} title="" onClick={() => {}} />
          <form>
            <input
              value={input}
              placeholder="Start a post"
              onChange={(e) => setInput(e.target.value)}
              type="text"
            />
            <button onClick={sendPost} type="submit">
              Send
            </button>
          </form>
        </div>
        <div className="feed__inputOptions">
          <InputOption Icon={ImageIcon} title="Photo" color="#70B5F9" />
          <InputOption Icon={SubscriptionsIcon} title="Video" color="#E7A33E" />
          <InputOption Icon={EventNoteIcon} title="Event" color="#C0CBCD" />
          <InputOption
            Icon={CalendarViewDayIcon}
            title="Write"
            color="#7FC15E"
          />
        </div>
      </div>
      <FlipMove>{posts?.map((post)=>post)}</FlipMove>
    </div>
  );
}

export default Feed;
