import Graph from "react-graph-vis";
import React, { useEffect, useState, useCallback } from "react";
import Color from "color";
import ReactDOM from "react-dom";
import "./Graph.css";
import { useParams, Link } from "react-router-dom";

import { getImpactsByProject } from "apis/impactApi";
import { getOutComeApi } from "apis/outComeApi";
import { getOutPutApi } from "apis/outPutApi";
import { getActivityApi } from "apis/activityApi";

const NetworkGraph2 = (props) => {
  const [graph, setGraph] = useState({
    nodes: [],
    edges: [],
  });
  const { idProject, multimedia } = useParams();

  const getDataFromApi = useCallback(async () => {
    const { impacts } = await getImpactsByProject(idProject);
    const impactsIds = impacts?.map((impact) => impact?._id);
    const { outcomes } = await getOutComeApi(impactsIds);
    const outcomesIds = outcomes?.map((outcome) => outcome?._id);
    const { outputs } = await getOutPutApi(outcomesIds);
    const outputsIds = outputs?.map((output) => output?._id);
    const { activities } = await getActivityApi(outputsIds);
    let nodes = [];
    let edges = [];
    impacts?.map((impact) => {
      nodes.push({
        ...impact,
        ...{
          id: impact?._id,
          level: 0,
          margin: 20,
          shape: "box",
          color: "#34495E",
          font: {
            color: "white",
          },
        },
      });
    });
    outcomes?.map((outcome) => {
      nodes.push({
        ...outcome,
        ...{
          id: outcome?._id,
          level: 1,
          margin: 20,
          shape: "box",
          color: "#3f51b5",
          font: {
            color: "white",
          },
        },
      });
    });
    outcomes?.map((outcome) => {
      outcome?.impacts?.map((impactId) => {
        edges.push({ from: impactId, to: outcome?._id });
      });
    });

    outputs?.map((output) => {
      nodes.push({
        ...output,
        ...{
          id: output?._id,
          level: 2,
          margin: 20,
          shape: "box",
          color: "#1ABC9C",
          font: {
            color: "white",
          },
        },
      });
    });
    outputs?.map((output) => {
      output?.outComes?.map((outcomeId) => {
        edges.push({ from: outcomeId, to: output?._id });
      });
    });
    console.log(activities, "activities");
    activities?.map((activity) => {
      nodes.push({
        ...activity,
        ...{
          id: activity?._id,
          level: 3,
          margin: 20,
          shape: "box",
          color: "#DC7633",
          font: {
            color: "white",
          },
        },
      });
    });
    activities?.map((activity) => {
      activity?.outPuts?.map((outputId) => {
        edges.push({ from: outputId, to: activity?._id });
      });
    });
    setGraph({
      ...graph,
      nodes: nodes,
      edges: edges,
    });
  }, []);

  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);

  const options = {
    autoResize: true,
    height: "600",
    width: "100%",
    interaction: {
      navigationButtons: true,
      keyboard: true,
      tooltipDelay: 50,
    },
    layout: {
      randomSeed: undefined,
      improvedLayout: true,
      clusterThreshold: 150,
      hierarchical: {
        enabled: false,
        levelSeparation: 150,
        nodeSpacing: 100,
        treeSpacing: 200,
        blockShifting: true,
        edgeMinimization: true,
        parentCentralization: true,
        // direction: 'UD',        // UD, DU, LR, RL
        sortMethod: "hubsize", // hubsize, directed
        shakeTowards: "leaves", // roots, leaves
      },
    },

    nodes: {
      shape: "box",
      widthConstraint: 120,
      heightConstraint: 72,
      shapeProperties: {
        borderRadius: 20,
      },
    },
    edges: {
      smooth: {
        type: "cubicBezier",
        forceDirection: "horizontal",
        roundness: 0.9,
      },
      color: "white",
    },
  };

  function randomColor() {
    const red = Math.floor(Math.random() * 256)
      .toString(16)
      .padStart(2, "0");
    const green = Math.floor(Math.random() * 256)
      .toString(16)
      .padStart(2, "0");
    const blue = Math.floor(Math.random() * 256)
      .toString(16)
      .padStart(2, "0");
    return `#${red}${green}${blue}`;
  }

  var activities = [
    {
      id: 1,
      x: 0,
      y: 0,
      label: "Activity 1 ",
      title: "user6 \n CTO",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#33FFDE",
      widthConstraint: 200,
      heightConstraint: 20,
    },

    {
      id: 2,
      x: 0,
      y: 100,
      label: "Activity 2",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#33FFDE",
      widthConstraint: 200,
      heightConstraint: 20,
    },

    {
      id: 3,
      x: 0,
      y: 200,
      label: "Activity 3",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#33FFDE",
      widthConstraint: 200,
      heightConstraint: 20,
    },

    {
      id: 4,
      x: 0,
      y: 300,
      label: "Activity 4",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#33FFDE",
      widthConstraint: 200,
      heightConstraint: 20,
    },
  ];
  var outputs = [
    {
      id: 10,
      x: 300,
      y: 0,
      label: "output 1 ",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#33F3FF",
      widthConstraint: 200,
      heightConstraint: 50,
    },

    {
      id: 11,
      x: 300,
      y: 100,
      label: "output 2",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#33F3FF",
      widthConstraint: 200,
      heightConstraint: 50,
    },

    {
      id: 12,
      x: 300,
      y: 200,
      label: "output 3",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#33F3FF",
      widthConstraint: 200,
      heightConstraint: 50,
    },

    {
      id: 14,
      x: 300,
      y: 300,
      label: "Activity 4",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#33F3FF",
      widthConstraint: 200,
      heightConstraint: 50,
    },
  ];

  var outcomes = [
    {
      id: 20,
      x: 600,
      y: 0,
      label: "outcome 1 ",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      // color:"#338DFF",
      widthConstraint: 200,
      heightConstraint: 100,
    },

    {
      id: 21,
      x: 600,
      y: 150,
      label: "outcome 2",
      title: "user5 \n Data Analyst",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      //  color:"#338DFF",
      widthConstraint: 200,
      heightConstraint: 100,
    },

    {
      id: 22,
      x: 600,
      y: 300,
      label: "output 3",
      title: "user5 \n Data Analyst",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#338DFF",
      widthConstraint: 200,
      heightConstraint: 100,
    },
  ].map((c, i) => ({
    ...c,
    color: {
      border: Color("#ffffff")
        .darken(0.2)
        .hex(),
      background: "#ffffff",
      highlight: {
        border: Color("#ffffff")
          .darken(0.3)
          .hex(),
        background: Color("#ffffff")
          .darken(0.2)
          .hex(),
      },
      hover: {
        border: Color("#ffffff")
          .darken(0.3)
          .hex(),
        background: Color("#ffffff")
          .darken(0.2)
          .hex(),
      },
    },
  }));

  var impacts = [
    {
      id: 30,
      x: 900,
      y: 150,
      label: "impact 1",
      shape: "box",
      size: 50,
      mass: 1,
      physics: false,
      color: "#240EA9",
      widthConstraint: 200,
      heightConstraint: 200,
      font: {
        color: "white",
      },
    },
  ];

  const [state, setState] = useState({
    counter: 5,
    graph: {
      nodes: [...activities, ...outputs, ...outcomes, ...impacts],
      edges: [
        { from: 10, to: 20, width: 2 },
        { from: 11, to: 21, width: 2 },
        { from: 2, to: 10, width: 2 },
        { from: 2, to: 11, width: 2 },
        { from: 2, to: 12, width: 2 },
        { from: 3, to: 6, width: 2 },
        { from: 20, to: 30, width: 2 },
        { from: 21, to: 30, width: 2 },
        { from: 22, to: 30, width: 2 },
      ],
    },
    events: {
      select: ({ nodes, pointer: { canvas } }) => {
        // const position = nodePosition(2);
        console.log("Selected nodes:" + nodes);
        console.log("Selected node: " + nodes + canvas.x + " " + canvas.y);
      },
      // doubleClick: ({ pointer: { canvas } }) => {
      //   createNode(canvas.x, canvas.y);
      // }
    },
  });
  const { events } = state;

  const positions = graph;
  console.log(positions);

  return (
    <div className="network-graph">
      <Graph graph={graph} options={options} events={events} />
    </div>
  );
};

export default NetworkGraph2;
