import React, { useState, useEffect, useCallback } from "react";
import "./ExpertDetailBody.css";
import ReactDOM from "react-dom";
import "./ExpertDetailHeader.css";
import SimpleMap from "./SimpleMap";
import CardInfoExpert from "../general/CardInfoExpert";
import { useParams } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { getOneExpertArticle } from "../../../../src/apis/ExpertArticleApi";

const ExpertDetailBody = ({ expert }) => {
  // const { ExpertArticleId } = useParams();
  // const history = useHistory();
  // const [expert, setExpert] = useState();

  // const getDataFromApi = useCallback(async () => {
  //   const exp = await getOneExpertArticle(ExpertArticleId);
  //   setExpert(expert);
  // }, [ExpertArticleId]);

  // useEffect(() => {
  //   getDataFromApi();
  // }, [getDataFromApi]);

  return (
    <>
      <div className="container mt-5">
        <div className="row">
          <div className="col-12 col-sm-6 justify-content-end">
            <h1
              style={{
                color: "#3f51b5",
                fontSize: "85px",
                lineHeight: "150px",
                fontWeight: "bold",
              }}
            >
              {expert.fullname}
            </h1>
          </div>
          <img
            className="rounded-circle img-top"
            src={expert.image_url}
            alt="Card cap"
          />
        </div>
      </div>

      {expert && (
        <>
          <div className="container mt-5  ">
            <h1 className="heading_background  mt-5 mb-5 pb-3 pt-3">
              Expert's<span className="sub-heading"> Article</span>
            </h1>{" "}
            <CardInfoExpert data={expert} />
          </div>
        </>
      )}

      <div className="container mt-5  ">
        <h1 className="heading_background  mt-5 mb-5 pb-3 pt-3">
          Expert's<span className="sub-heading"> Contact</span>
        </h1>{" "}
        <div className="row">
          <div className="col-4 col-sm-6 ">
            <ul className="list-unstyled">
              <div className="col-12 col-sm-6 ">
                <li style={{ marginTop: "50px" }}>
                  <img
                    style={{ width: "34px" }}
                    alt="yellow icon"
                    src="/assets/icons/contact-icon-phone.png"
                  />{" "}
                  <span className="ml-4">+31 661361139</span>
                </li>
              </div>
              <div className="col-4 col-sm-6 ">
                <li style={{ marginTop: "20px" }}>
                  <img
                    style={{ width: "34px" }}
                    alt="yellow icon"
                    src="/assets/icons/contact-icon-mail.png"
                  />{" "}
                  <span className="ml-4">{expert.email}</span>
                </li>
              </div>
              <li style={{ marginTop: "20px" }}>
                <img
                  style={{ width: "34px" }}
                  alt="yellow icon"
                  src="/assets/icons/contact-icon-pin.png"
                />{" "}
                <span className="ml-4">Address </span>
              </li>
            </ul>
          </div>

          <div className="col-12 ">
            <SimpleMap />
          </div>
        </div>
      </div>
    </>
  );
};

export default ExpertDetailBody;

