import React, { forwardRef } from "react";
import { Avatar } from "@material-ui/core";
import moment from "moment";
import ThumbUpAltOutlinedIcon from "@material-ui/icons/ThumbUpAltOutlined";
import ChatOutlinedIcon from "@material-ui/icons/ChatOutlined";
import ShareOutlinedIcon from "@material-ui/icons/ShareOutlined";
import SendOutlinedIcon from "@material-ui/icons/SendOutlined";
import InputOption from "../inputOption/InputOption";
import "./Post.css";

const Post = forwardRef(
  (
    {
      name,
      description,
      message,
      photoUrl,
      isLike,
      createdAt,
      likes,
      onLike,
      disableLike,
      postImage,
    },
    ref
  ) => {
    return (
      <div ref={ref} className="post border">
        <div className="post__header">
          <Avatar src={photoUrl}>{name[0]}</Avatar>
          <div className="post__info">
            <h2>{name}</h2>
            <p className="m-0">{description}</p>
            <p className="m-0">
              {moment(createdAt)
                .startOf("minute")
                .fromNow()}
            </p>
          </div>
        </div>
        <div className="post__body">
          <p>{message}</p>
          {postImage && <img src={postImage} width="100%" />}
        </div>
        <div className="ml-3 mt-2" style={{ color: "gray", fontSize: "13px" }}>
          <ThumbUpAltOutlinedIcon
            className="mb-1"
            style={{ color: "#0e76a8", width: "15px" }}
          />{" "}
          {likes.length}
        </div>
        <div className="post__buttons">
          <InputOption
            Icon={ThumbUpAltOutlinedIcon}
            title="Like"
            color={isLike ? "#0e76a8" : "gray"}
            onLike={onLike}
            disableLike={disableLike}
          />
          <InputOption Icon={ChatOutlinedIcon} title="Comment" color="gray" />
          <InputOption Icon={ShareOutlinedIcon} title="Share" color="gray" />
          <InputOption Icon={SendOutlinedIcon} title="Send" color="gray" />
        </div>
      </div>
    );
  }
);

export default Post;
