import React, { useEffect, useState, useCallback } from "react";
import { useParams, Link } from "react-router-dom";
import Graph from "react-graph-vis";
import CountryHeader from "../../components/layout/header/CountryHeader";
import PublicFooter from "../../components/layout/footer/PublicFooter";
import ShowMoreDetails from "../../components/aidchannel/project/ShowMoreDetails";
import { getImpactsByProject } from "apis/impactApi";
import { getOutComeApi } from "apis/outComeApi";
import { getOutPutApi } from "apis/outPutApi";
import { getActivityApi } from "apis/activityApi";
import NetworkGraph2 from "./NetworkGraph2";

const ShowMoreDetailsProject2 = (props) => {
  const [graph, setGraph] = useState({
    nodes: [],
    edges: [],
  });
  const { idProject, multimedia } = useParams();

  const getDataFromApi = useCallback(async () => {
    const { impacts } = await getImpactsByProject(idProject);
    const impactsIds = impacts?.map((impact) => impact?._id);
    const { outcomes } = await getOutComeApi(impactsIds);
    const outcomesIds = outcomes?.map((outcome) => outcome?._id);
    const { outputs } = await getOutPutApi(outcomesIds);
    const outputsIds = outputs?.map((output) => output?._id);
    const { activities } = await getActivityApi(outputsIds);

    let nodes = [];
    let edges = [];
    impacts?.map((impact) => {
      nodes.push({
        ...impact,
        ...{
          id: impact?._id,
          level: 0,
          margin: 20,
          shape: "box",
          color: "#34495E",
          font: {
            color: "white",
          },
        },
      });
    });
    outcomes?.map((outcome) => {
      nodes.push({
        ...outcome,
        ...{
          id: outcome?._id,
          level: 1,
          margin: 20,
          shape: "box",
          color: "#3f51b5",
          font: {
            color: "white",
          },
        },
      });
    });

    outcomes?.map((outcome) => {
      outcome?.impacts?.map((impactId) => {
        edges.push({ from: impactId, to: outcome?._id });
      });
    });

    outputs?.map((output) => {
      nodes.push({
        ...output,
        ...{
          id: output?._id,
          level: 2,
          margin: 20,
          shape: "box",
          color: "#1ABC9C",
          font: {
            color: "white",
          },
        },
      });
    });

    outputs?.map((output) => {
      output?.outComes?.map((outcomeId) => {
        edges.push({ from: outcomeId, to: output?._id });
      });
    });
    console.log(activities, "activities");
    activities?.map((activity) => {
      nodes.push({
        ...activity,
        ...{
          id: activity?._id,
          level: 3,
          margin: 20,
          shape: "box",
          color: "#DC7633",
          font: {
            color: "white",
          },
        },
      });
    });

    activities?.map((activity) => {
      activity?.outPuts?.map((outputId) => {
        edges.push({ from: outputId, to: activity?._id });
      });
    });
    setGraph({
      ...graph,
      nodes: nodes,
      edges: edges,
    });
  }, []);

  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);

  const details = [
    {
      expected_results: "expected_results",
      beneficiaries: "Beneficiaries",
      key_experts: "key_experts",
      localization: "localization",
      thematic: "thematic",
      kpi: "kpi",
    },
  ];

  const options = {
    layout: {
      hierarchical: true,
    },
    interaction: {
      zoomView: false,
    },
    edges: {
      color: "#000000",
    },
    physics: {
      forceAtlas2Based: {
        gravitationalConstant: -26,
        centralGravity: 0.005,
        springLength: 230,
        springConstant: 0.18,
      },
      maxVelocity: 146,
      solver: "forceAtlas2Based",
      timestep: 0.35,
      stabilization: {
        enabled: true,
        iterations: 2000,
        updateInterval: 25,
      },
    },

    height: "500px",
  };
  const optionsgraph2 = {
    autoResize: true,
    height: "600",
    width: "100%",
    interaction: {
      navigationButtons: true,
      keyboard: true,
      tooltipDelay: 50,
    },
    layout: {
      randomSeed: undefined,
      improvedLayout: true,
      clusterThreshold: 150,
      hierarchical: {
        enabled: false,
        levelSeparation: 150,
        nodeSpacing: 100,
        treeSpacing: 200,
        blockShifting: true,
        edgeMinimization: true,
        parentCentralization: true,
        // direction: 'UD',        // UD, DU, LR, RL
        sortMethod: "hubsize", // hubsize, directed
        shakeTowards: "leaves", // roots, leaves
      },
    },
  };

  const events = {};

  return (
    <>
      <CountryHeader />
      <div className="container">
        <h1 className="heading_background  mt-5 mb-5 pb-3 pt-3">
          Details<span className="sub-heading"> of the project</span>
        </h1>
        {details.map((item, index) => (
          <ShowMoreDetails key={index} index={index} data={item} />
        ))}
        <h1 className="heading_background  mt-5 mb-5 pb-3 pt-3">
          Graph<span className="sub-heading"> of the project</span>
        </h1>
        <ul className="nav nav-tabs">
          <li className="nav-item">
            <Link
              to={`/projectdetails/${multimedia}/${idProject}/more`}
              className={`nav-link ${multimedia === "graph1" && "active"}`}
            >
              Simple Graph
            </Link>
          </li>
          <li className="nav-item">
            <Link
              to={`/projectdetails/${multimedia}/${idProject}/more`}
              className={`nav-link ${multimedia === "graph2" && "active"}`}
            >
              Horizontal Graph
            </Link>
          </li>
        </ul>
        {multimedia === "graph1" && (
          <Graph
            key={Math.random()}
            graph={graph}
            options={options}
            events={events}
            getNetwork={(network) => {
              //  if you want access to vis.js network api you can set the state in a parent component using this property
            }}
          />
        )}
        {multimedia === "graph2" && (
          <Graph
            key={Math.random()}
            graph={graph}
            options={options}
            events={events}
            getNetwork={(network) => {}}
          />
        )}
      </div>

      <PublicFooter />
    </>
  );
};

export default ShowMoreDetailsProject2;
