import React, { useState, useEffect, useCallback, useRef } from "react";
import { useParams } from "react-router-dom";
import { getProjectsOfMonthApi } from "../../apis/projectApi";
import YoutubeVideo from "../../components/aidchannel/youtubeVideos/YoutubeVideo";
import Tweets from "../../components/aidchannel/tweets/Tweets";
import CardInfo from "../../components/aidchannel/general/CardInfo";
import CardInfo2 from "../../components/aidchannel/general/CardInfo2";
import CountryHeader from "../../components/layout/header/CountryHeader";
import PublicFooter from "../../components/layout/footer/PublicFooter";
import { getVideosByCountry } from "../../apis/youtubeApi";
import { useHistory } from "react-router-dom";
import { getAllTweetsByCountryPagination } from "apis/twitterApi";
import useIntersectionObserver from "../../helpers/useIntersectionObserver";
import { useInfiniteQuery } from "react-query";
import Post from "../../components/aidchannel/tweets/Post";
const HomeCountry = (props) => {
  const [project, setProject] = useState([]);
  const [videos, setVideos] = useState();
  const history = useHistory();
    const [limit] = useState(4);
    const loadMoreButtonRef = useRef();
     const { codeCountry } = useParams();
      const [test, setTest] = useState(false);
  const {
    data,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
  } = useInfiniteQuery([codeCountry, limit, test], getAllTweetsByCountryPagination, {
    getNextPageParam: (lastPage, pages) => {
      if (lastPage.data.length === limit) return parseInt(lastPage.page) + 1;
      return false;
    },
  });
  useIntersectionObserver({
    target: loadMoreButtonRef,
    onIntersect: fetchNextPage,
    enabled: hasNextPage,
  });


  const getDataFromApi = useCallback(async () => {
    const proj = await getProjectsOfMonthApi(1, 1);
    setProject(proj.data[0]);
    const videos = await getVideosByCountry(codeCountry, 4, 1);
    setVideos(videos);
  }, [codeCountry]);

  useEffect(() => {
    getDataFromApi();
  }, [codeCountry, getDataFromApi]);

  const Tweeter = [
    "1411307008167399426",
    "1346737900587782146",
    "1339118173988204545",
  ];
  const article = [
    {
      name: "PARTNERSHIPS AND PROJECTS",
      description:
        "PARTNERSHIPS AND PROJECTS Family Planning: Partnerships Partnerships The U.S. Agency for International Development (USAID) works with a wide range of public and private partners to accelerate development  and ensure we are using resources efficiently. USAID’s partnerships promote ...",
      image_url:
        "https://assets.maccarianagency.com/the-front/photos/coworking/place3.jpg",
    },
    {
      name: "WOMEN ACHIEVING THEIR DREAMS",
      description:
        "PARTNERSHIPS AND PROJECTS Family Planning: Partnerships Partnerships The U.S. Agency for International Development (USAID) works with a wide range of public and private partners to accelerate development  and ensure we are using resources efficiently. USAID’s partnerships promote ...",
      image_url:
        "https://share.america.gov/wp-content/uploads/2021/03/USAID-Women-Leaders-Climate-Crisis-49534646426_44e4a8da7c_o.jpg",
    },
    {
      name: "CROIX-ROUGE",
      description:
        "PARTNERSHIPS AND PROJECTS Family Planning: Partnerships Partnerships The U.S. Agency for International Development (USAID) works with a wide range of public and private partners to accelerate development  and ensure we are using resources efficiently. USAID’s partnerships promote ...",
      image_url:
        "https://miro.medium.com/max/1400/1*q_sc5BCE-VIHxnX4teOrvw.jpeg",
    },
  ];

  return (
    <>
      <CountryHeader />
      <div className="container">
        <h1 className="heading_background  mt-5 mb-5 pb-3 pt-3">
          Project<span className="sub-heading"> of the month</span>
        </h1>
        <CardInfo
          data={project}
          url={`/country/${codeCountry}/projectOfMonthDetails`}
          index={0}
        />
        <div className="col-6">
          <button className="btn btn-view-more" style={{ marginTop: "10px" }}>
            More projects
          </button>
        </div>

        <h1 className="heading_background  mt-5 mb-5 pb-3 pt-3">
          Youtube<span className="sub-heading"> Videos</span>
        </h1>

        <div className="row">
          {videos?.map((video, index) => (
            <div key={index} className="col-12  col-md-6 ">
              <YoutubeVideo key={index} url={video?.video_url} />
            </div>
          ))}
        </div>

        <div className="row">
          <div className="col-6 mt-1">
            <button
              onClick={() =>
                history.push(`/country/${codeCountry}/view-more-youtube`)
              }
              type="button"
              className="btn btn-view-more"
              style={{ marginTop: "10px", marginLeft: "30px" }}
            >
              More Videos
            </button>{" "}
          </div>
        </div>

        <h1 className="container heading_background  mt-5 mb-5 pb-3 pt-3">
          Tweets<span className="sub-heading"> USAID</span>
        </h1>

        <div className="row">
          {data?.pages.map((group, i) => (
            <React.Fragment key={i}>
              {group.data.map((tweet, index) => (
                <div
                  key={index}
                  className="col-12  p-2 col-lg-5 mx-auto mt-3 border"
                >
                  <a
                    style={{ textDecoration: "none" }}
                    href={`https://twitter.com/${tweet?.twitter_username}/status/${tweet?.tweet_id}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    <Post
                      displayName={tweet?.name}
                      username={tweet?.twitter_username}
                      verified={true}
                      text={tweet?.body}
                      avatar={tweet?.avatar_id}
                      image={tweet?.photos?.length > 0 && tweet.photos[0]}
                    />
                  </a>
                </div>
              ))}
            </React.Fragment>
          ))}
        </div>

        <div className="row">
          <div className="col-6">
            <button
              onClick={() =>
                history.push(`/country/${codeCountry}/view-more-tweet`)
              }
              type="button"
              className="btn btn-view-more"
              style={{ marginTop: "20px", marginLeft: "30px" }}
            >
              More Tweets
            </button>
          </div>
        </div>

        <h1 className="container heading_background  mt-5 mb-5 pb-3 pt-3">
          Articles <span className="sub-heading"> Related</span>
        </h1>
        {article.map((item, index) => (
          <CardInfo2 key={index} data={item} index={index} />
        ))}
        <div className="row">
          <div className="col-6">
            <button
              onClick={() =>
                history.push(`/country/${codeCountry}/view-more-article`)
              }
              type="button "
              className="btn btn-view-more "
              style={{ marginBottom: "20px"}}
            >
              More Articles
            </button>{" "}
          </div>
        </div>
      </div>
      <PublicFooter />
    </>
  );
};

export default HomeCountry;
