import React, { useState, useEffect, useCallback } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useForm } from "react-hook-form";

import { getCountryByCode } from "../../../apis/countryApi";
import "./OrganizationForm.css";
import {
  AddExpert,
  getAllHeadExpertsWithIdAndName,
  getExpert,
  updateExpert,
} from "apis/userApi";

const ExpertForm = () => {
  const history = useHistory();
  const { codeCountry, idExpert } = useParams();

  const [content, setContent] = useState("");

  const {
    register,
    handleSubmit,
    setValue,
    //formState: { errors },
  } = useForm();

  const getDataFromApi = useCallback(async () => {
    const experts = await getAllHeadExpertsWithIdAndName();

    if (idExpert !== undefined) {
      const expert = await getExpert(idExpert);

      setValue("fullname", idExpert?.fullname);
      setValue("email", idExpert?.email);
      setValue("image_url", idExpert?.image_url);
    }
  }, [idExpert, setValue]);

  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);

  const onSubmit = async (data) => {
    const country = await getCountryByCode(codeCountry);
    const expert = await getExpert(idExpert);
    data.country = country._id;

    data.fullname = expert?.fullname;
    data.phone = expert?.phone;
    data.adress = expert?.adress;
    data.job_title = expert?.job_title;
    data.image_url = expert?.image_url;

    // if (data.twitter_username === "") data.twitter_username = null;
    // else data.twitter_username = data?.twitter_username?.substring(20);
    // if (data.youtube_url === "") data.youtube_url = null;

    // if (data.organization_size === "") data.organization_size = null;

    // if (content === "") data.description = null;
    // else data.description = content;

    if (idExpert !== undefined) {
      await updateExpert(idExpert, data);
    } else {
      await AddExpert(data);
    }

    history.push(`/web-master/experts/${codeCountry}`);
  };

  return (
    <div className="container">
      <form>
        <div className="row pb-5">
          <div className="centralize mt-4 col-12 col-lg-6">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="fullname"
                spellCheck="false"
                {...register("fullname", {
                  required: "Name required",
                })}
              />
              <span className="placeholder">Full Name</span>
            </div>
          </div>

          <div className="form-group">
            <label className="ml-4 logo-file" htmlFor="image_url">
              Image
            </label>
            <input
              type="file"
              className="form-control-file ml-4 shadow-none logo-file"
              id="image_url"
              name="image_url"
              multiple={false}
              {...register("image_url")}
            />
          </div>

          <div className="col-12 col-lg-6 centralize mt-4">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="email"
                spellCheck="false"
                {...register("email", {
                  required: "Email Required",
                  pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,

                })}
              />
              <span className="placeholder">Email</span>
            </div>
          </div>

          <div className="col-12 col-lg-6 centralize mt-4">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="adress"
                spellCheck="false"
                {...register("adress")}
              />
              <span className="placeholder">Address</span>
            </div>
          </div>
          <div className="col-12 col-lg-6 centralize mt-4">
            <div className="input-block">
              {" "}
              <input
                className="input-org-form"
                type="text"
                id="job_title"
                spellCheck="false"
                {...register("job_title")}
              />
              <span className="placeholder">Job of the expert</span>
            </div>
          </div>
          <div className="col-12 col-lg-6 centralize mt-4">
            <div className="input-block">
              {" "}
              <input
                className="input-org-form"
                type="text"
                id="phone"
                spellCheck="false"
                {...register("phone")}
              />
              <span className="placeholder">Phone</span>
            </div>
          </div>
          <button
            onClick={handleSubmit(onSubmit)}
            type="submit"
            className="btn btn-primary w-100 h-25 submit-login my-5 "
          >
            {idExpert !== undefined ? "Edit Expert" : "Add Expert"}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ExpertForm;
