import Flag from "react-world-flags";
import React, { useState, useEffect, useCallback } from "react";
import { useHistory, useParams, Link } from "react-router-dom";
import { getAllProjectsByCountry, deleteProject } from "../../apis/projectApi";
import TableProject from "components/aidchannel/webmaster/TableProject";
import TableAdmin from "components/aidchannel/superadmin/MtableAdmin";

const ListProjectsNonValides = () => {
  const { codeCountry, multimedia } = useParams();
  const history = useHistory();
  const [projects, setprojects] = useState([]);
  const getDataFromApi = useCallback(async () => {
    const project = await getAllProjectsByCountry(codeCountry);
    setprojects(project?.reverse());
  }, [codeCountry]);
  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);
  const deletePjt = async (project) => {
    if (window.confirm(`Are you sure you wish to delete ${project.name} ?`)) {
      try {
        await deleteProject(project._id);
        const newProjectList = projects.filter(function(item) {
          if (item["_id"] === project?._id) return false;
          return true;
        });
        setprojects(newProjectList);
      } catch (error) {
        console.log(error);
      }
    }
  };

  return (
    <div className="container">
      <>
        <h1>
          <Flag
            code={codeCountry}
            height="70"
            width="70"
            style={{ marginRight: "2px" }}
            className="flag"
          />{" "}
          Projects Non Valides
        </h1>
        <ul className="nav nav-tabs">
          <li className="nav-item">
            <Link
              to={`/web-master/projectsvalides/${codeCountry}`}
              className={`nav-link ${multimedia === "projects valides" &&
                "active"}`}
            >
              Projects Valides
            </Link>
          </li>
          <li className="nav-item">
            <Link
              to={`/web-master/projectsNonvalides/${codeCountry}`}
              className={`nav-link ${multimedia === "projects non valides" &&
                "active"}`}
            >
              Projects non valides
            </Link>
          </li>
        </ul>
        {/* </> */}
        {multimedia === "projects valides" && <TableProject />}
        {multimedia === "projects non valides" && <TableAdmin />}
        {/* </div> */}
        <button
          onClick={() => history.push(`/web-master/add-project/${codeCountry}`)}
          className="btn btn-primary ml-3 my-3 shadow-none border-none"
        >
          Add
        </button>
        <TableProject />
      </>
    </div>
  );
};

export default ListProjectsNonValides;
