          shape: "box",
          size: 50,
          mass: 1,
          physics: false,
          color: "#338DFF",
          widthConstraint: 200,
          heightConstraint: 100,
        },
      });
      outcomeY = outcomeY + 150;
    });
    outcomes?.map((outcome) => {
      outcome?.impacts?.map((impactId) => {
        edges.push({ from: impactId, to: outcome?._id });
      });
    });

    outputs?.map((output) => {
      nodes.push({
        ...output,
        ...{
          id: output?._id,
          // x: 300,
          // y: outputY + 100,
          label: output?.label,
          shape: "box",
          size: 50,
          mass: 1,
          physics: false,
          color: "#33F3FF",
          widthConstraint: 200,
          heightConstraint: 50,
        },
      });
      outputY = outputY + 100;
    });
    outputs?.map((output) => {
      output?.outComes?.map((outcomeId) => {
        edges.push({ from: outcomeId, to: output?._id });
      });
    });
    console.log(activities, "activities");
    activities?.map((activity) => {
      nodes.push({
        ...activity,
        ...{
          id: activity?._id,
          // x: 0,
