          label: activity?.label,
          shape: "box",
          size: 50,
          mass: 1,
          physics: false,
          color: "#33FFDE",
          widthConstraint: 200,
          heightConstraint: 20,
        },
      });
      activityY = activityY + 100;
    });
    activities?.map((activity) => {
      activity?.outPuts?.map((outputId) => {
        edges.push({ from: outputId, to: activity?._id });
      });
    });
    setGraph({
      ...graph,
      nodes: nodes,
      edges: edges,
    });
