    },

    nodes: {
      shape: "box",
      widthConstraint: 120,
      heightConstraint: 72,
      shapeProperties: {
        borderRadius: 20,
      },
    },
    edges: {
      smooth: {
        type: "cubicBezier",
        forceDirection: "horizontal",
        roundness: 0.9,
      },
      color: "white",
    },
  };

  function randomColor() {
    const red = Math.floor(Math.random() * 256)
      .toString(16)
      .padStart(2, "0");
    const green = Math.floor(Math.random() * 256)
      .toString(16)
      .padStart(2, "0");
    const blue = Math.floor(Math.random() * 256)
