import React, { useState, useRef, useEffect, useCallback } from "react";
import { useInfiniteQuery } from "react-query";
import { useParams, useHistory } from "react-router-dom";
import InputSearch from "../../components/aidchannel/general/InputSearch";
import PublicHeader from "../../components/layout/header/PublicHeader";
import PublicFooter from "../../components/layout/footer/PublicFooter";
import Tweets from "../../components/aidchannel/tweets/Tweets";
import {
  getAllTweetsByCountryPagination,
  getAllTweetsByCountryPaginationSearch,
  getTweetsAccepted,
} from "../../apis/twitterApi";
import Post from "../../components/aidchannel/tweets/Post";
import useIntersectionObserver from "../../helpers/useIntersectionObserver";

const ViewMoreTweet = () => {
  const [searchText, setSearchText] = useState("");
  const [limit] = useState(9);
  const loadMoreButtonRef = useRef();
const history = useHistory();
  const { codeCountry } = useParams();

  const [test, setTest] = useState(false);

  const {
    data,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
  } = useInfiniteQuery(
    [codeCountry, limit, test],
    getAllTweetsByCountryPagination,
    {
      getNextPageParam: (lastPage, pages) => {
        if (lastPage.data.length === limit) return parseInt(lastPage.page) + 1;
        return false;
      },
    }
  );
  useIntersectionObserver({
    target: loadMoreButtonRef,
    onIntersect: fetchNextPage,
    enabled: hasNextPage,
  });

  const tweeter = [
    "1411307008167399426",
    "1346737900587782146",
    "1339118173988204545",
    "1346737900587782146",
  ];

  return (
    <>
      <PublicHeader />
      <div className="container">
        <h1 className="heading my-4">
          More
          <span className="sub-heading"> Tweets </span>
        </h1>
        <form
          onSubmit={() =>
            history.push(`/Tweets/${codeCountry}/${searchText}`)
          }
          className="form-inline my-2 my-lg-0 w-100 h-100"
        >
          <div className="form-group has-search  w-md-50 w-xs-100">
            <span className="fa fa-search form-control-feedback"></span>
            <input
              onChange={(e) => setSearchText(e.target.value)}
              value={searchText}
              type="text"
              className="form-control input-search "
              placeholder="Search for Tweets"
              style={{
                width: "100%",
                border: "none",
                boxShadow: "rgba(0, 0, 0, 0.24) 0px 3px 8px",
              }}
            />
          </div>
        </form>

        {/* <div className="row">
          {tweeter.map((item, index) => (
            <div key={index} className="col-12 col-md-6 col-lg-4 my-4">
              <Tweets id={item} key={index} />
            </div>
          ))}
        </div> */}

        <div className="row">
          {data?.pages.map((group, i) => (
            <React.Fragment key={i}>
              {group.data.map((tweet, index) => (
                <div
                  key={index}
                  className="col-12  p-2 col-lg-5 mx-auto mt-3 border"
                >
                  <a
                    style={{ textDecoration: "none" }}
                    href={`https://twitter.com/${tweet?.twitter_username}/status/${tweet?.tweet_id}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    <Post
                      displayName={tweet?.name}
                      username={tweet?.twitter_username}
                      verified={true}
                      text={tweet?.body}
                      avatar={tweet?.avatar_id}
                      image={tweet?.photos?.length > 0 && tweet.photos[0]}
                    />
                  </a>
                </div>
              ))}
            </React.Fragment>
          ))}
        </div>

        <div style={{ marginTop: "20px" }}>
          <button
            ref={loadMoreButtonRef}
            onClick={() => fetchNextPage()}
            disabled={!hasNextPage || isFetchingNextPage}
          >
            {isFetchingNextPage
              ? "Loading more ...."
              : hasNextPage
              ? "Load More"
              : "Nothing more to load"}
          </button>
        </div>
      </div>
      <PublicFooter />
    </>
  );
};

export default ViewMoreTweet;
