import TableOrganization from "components/aidchannel/webmaster/TableOrganization";
import React, { useState, useEffect, useCallback } from "react";
import { useHistory, useParams } from "react-router-dom";
import Flag from "react-world-flags";
import { getCountryByCode } from "../../apis/countryApi";
import {
  deleteOrganization,
  getAllOrganizationsByCountry,
} from "../../apis/organizationApi";
import MTableOrganization from "../../components/aidchannel/webmaster/MTableOrganization";
import MaterialTable from "material-table";
import { FcCalendar } from "react-icons/fc";
import { FaRegEdit } from "react-icons/fa";

import axios from "axios";
import urlImageAdapter from "helpers/urlImageAdapter";
import { deleteExpert } from "apis/userApi";

const ListAddedUsers = () => {
  //   const history = useHistory();
  const { codeCountry } = useParams();
  const [country, setCountry] = useState();
  const [organizations, setOrganizations] = useState([]);

  const orgTableRef = React.createRef();
  const history = useHistory();
  //   const { codeCountry } = useParams();

  const reloadTableData = () => {
    orgTableRef.current.onQueryChange();
  };

  const getDataFromApi = useCallback(async () => {
    const country = await getCountryByCode(codeCountry);
    setCountry(country);
    const subOrganization = await getAllOrganizationsByCountry(codeCountry);
    setOrganizations(subOrganization?.reverse());
  }, [codeCountry]);

  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi, codeCountry]);

  const deleteSubOrg = async (org) => {
    if (window.confirm(`Are you sure you wish to delete ${org.name} ?`)) {
      try {
        await deleteOrganization(org._id);
        const newOrganizationList = organizations.filter(function(item) {
          if (item["_id"] === org?._id) return false;
          return true;
        });
        setOrganizations(newOrganizationList);
      } catch (error) {
        console.log(error);
      }
    }
  };

  return (
    <div className="container">
      {country && (
        <>
          tableRef={orgTableRef}
          title="List of experts" columns=
          {[
            {
              field: "image_url",
              title: "Profil",
              render: (rowData) => (
                <img
                  className="img-fluid"
                  src={urlImageAdapter(rowData?.image_url)}
                  style={{
                    verticalAlign: "middle",
                    width: "50px",
                    height: "50px",
                    borderRadius: "50%",
                  }}
                />
              ),
              cellStyle: {
                width: "15%",
                textAlign: "center",
              },
            },
            {
              title: "Full Name",
              field: "fullname",
              cellStyle: {
                width: "25%",

                textAlign: "center",
              },
            },
            {
              title: "Email",
              field: "email",
              cellStyle: {
                width: "25%",

                textAlign: "center",
              },
            },
            {
              title: "Phone",
              field: "phone",
              cellStyle: {
                width: "25%",
                textAlign: "center",
              },
            },
            {
              title: "Expert of the month",
              field: "expert_of_month",
              cellStyle: {
                width: "25%",
                textAlign: "center",
              },
            },
          ]}
          data=
          {(query) =>
            new Promise((resolve, reject) => {
              let url = `${process.env.REACT_APP_BACKEND}user/expertsByCodeCountryPagination/${codeCountry}?`;
              url += "limit=" + query.pageSize;
              url += "&page=" + (query.page + 1);
              axios
                .post(url, {
                  searchText: query.search,
                })
                .then((result) => {
                  resolve({
                    data: result.data.data,
                    page: result.data.page - 1,
                    totalCount: result.data.totalCount,
                  });
                });
            })
          }
          options=
          {{
            filtering: false,
            search: true,
            sorting: false,
            headerStyle: {
              backgroundColor: "#0069d9",
              textAlign: "center",
              color: "white",
            },
            actionsColumnIndex: -1,
          }}
          actions=
          {[
            {
              icon: "refresh",
              tooltip: "Refresh Data",
              isFreeAction: true,
              onClick: () =>
                orgTableRef.current && orgTableRef.current.onQueryChange(),
            },

            {
              icon: () => <FaRegEdit size={30} color="#ffc107" />,
              tooltip: "Edit Expert",
              onClick: (event, expert) => {
                history.push(
                  `/web-master/edit-Expert/${codeCountry}/${expert?._id}`
                );
              },
            },
            {
              icon: () => <FcCalendar size={30} />,
              tooltip: "Expert of the month",
              onClick: (event, interview) => {
                history.push(
                  `/web-master/add-expert-article/${codeCountry}/${interview?._id}`
                );
              },
            },
            {
              icon: () => (
                <button className="btn btn-danger border-none shadow-none">
                  Delete
                </button>
              ),
              onClick: async (event, user) => {
                if (
                  window.confirm(
                    `Are you sure you wish to delete ${user.fullname} ?`
                  )
                ) {
                  try {
                    await deleteExpert(user?._id);
                    reloadTableData();
                    reloadTableData();
                  } catch (error) {
                    console.log(error);
                  }
                }
              },
            },
          ]}
          {/* </div> */}
          {/* <TableOrganization /> */}
        </>
      )}
    </div>
  );
};

export default ListAddedUsers;
