import React, { useState, useEffect } from "react";
import "./PublicFooter.css";
import { Link } from "react-router-dom";
import { getThematiques } from "../../../apis/thematiqueApi";
import { getOrganizationsType } from "../../../apis/organisationType";

const PublicFooter = () => {
  const [thematics, setThematics] = useState([]);
  const [organizationTypes, setOrganizationTypes] = useState([]);

  useEffect(() => {
    getDataFromApi();
  }, []);

  const getDataFromApi = async () => {
    const thematic = await getThematiques();
    setThematics(thematic);
    const orgType = await getOrganizationsType();
    setOrganizationTypes(orgType);
  };
  return (
    <div className="public-footer">
      <div className="container">
        <div className="row">
          <div className="col-12 col-md-2 "></div>
          <div className="col-md-4">
            <ul className="list">
              <li
                className="mb-4"
                style={{ fontSize: "20px", color: "#f9b934" }}
              >
                By Thematics
              </li>

              {thematics?.map((thematic, index) => (
                <li key={index}>
                  <Link
                    className="items"
                    style={{ color: "#cccccc", textDecoration: "none" }}
                    to={`/country/`}
                  >
                    {thematic.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>

          <div className="col-12 col-md-3">
            <ul className="list">
              <li
                className="mb-4"
                style={{ fontSize: "20px", color: "#f9b934" }}
              >
                By Donors
              </li>

              {organizationTypes?.map((org, index) => (
                <li key={index}>
                  <Link
                    className="items"
                    style={{ color: "#cccccc", textDecoration: "none" }}
                    to={`/country/`}
                  >
                    {org.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>

          <div className="col-12 col-md-3">
            <ul className="list">
              <li
                className="mb-4"
                style={{ fontSize: "20px", color: "#f9b934" }}
              >
                By Implementers
              </li>

              {organizationTypes?.map((org, index) => (
                <li key={index}>
                  <Link
                    className="items"
                    style={{ color: "#cccccc", textDecoration: "none" }}
                    to={`/country/`}
                  >
                    {org.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PublicFooter;
