import React from "react";
import "./authentication.css";
import { useForm } from "react-hook-form";

const LoginForm = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    alert(JSON.stringify(data));
  };

  return (
    <form>
      <div className="centralize">
        <div className="input-block">
          <input
            className="input"
            type="text"
            id="email"
            spellCheck="false"
            {...register("email", {
              required: "Email Required",
              pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
            })}
            required
          />
          <span className="placeholder">Email address</span>
          {errors.email && errors.email.type === "required" && (
            <div className="text-danger mt-2">You must enter your email</div>
          )}
          {errors.email && errors.email.type === "pattern" && (
            <div className="text-danger mt-2">You must enter a valid email</div>
          )}
        </div>
      </div>
      <div className="centralize mt-4">
        <div className="input-block">
          <input
            className="input"
            type="password"
            id="password"
            spellCheck="false"
            {...register("password", {
              required: "Password Required",
            })}
            required
          />
          <span className="placeholder">Password</span>
          {errors.password && errors.password.type === "required" && (
            <div className="text-danger mt-2">You must enter your password</div>
          )}
        </div>
      </div>

      <button
        onClick={handleSubmit(onSubmit)}
        type="submit"
        className="btn btn-primary form-control submit-login mt-4"
      >
        SIGN IN
      </button>
    </form>
  );
};

export default LoginForm;
