import ProjectCard from "components/aidchannel/project/ProjectCard";
import React, { useState, useRef } from "react";
import { useInfiniteQuery } from "react-query";
import { useParams } from "react-router-dom";
import {
    acceptProject,
   getAllProjectsByCountryPagination,
   refuseProject,

} from "../../apis/projectApi";

import useIntersectionObserver from "../../helpers/useIntersectionObserver";

const ProjectValidation = () => {
  const [limit] = useState(4);
  const { codeCountry } = useParams();
  const loadMoreButtonRef = useRef();
  const [test, setTest] = useState(false);

  const {
    data,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
  } = useInfiniteQuery(
    [codeCountry, limit, test],
    getAllProjectsByCountryPagination,
    {
      getNextPageParam: (lastPage, pages) => {
        if (lastPage.data.length === limit) return parseInt(lastPage.page) + 1;
        return false;
      },
    }
  );



  useIntersectionObserver({
    target: loadMoreButtonRef,
    onIntersect: fetchNextPage,
    enabled: hasNextPage,
  });

  const accept = async (projectId) => {
    await  acceptProject(projectId);
    setTest(!test);
  };
  const refuse = async (projectId) => {
    await refuseProject(projectId);
    setTest(!test);
  };

  return (
    <>
      <div className="row ">
        {data?.pages.map((group, i) => (
          <React.Fragment key={i}>
            {group.data.map((project, index) => (
              <div key={index} className="col-12 col-md-6 col-lg-4 my-4">
                <ProjectCard project={project} key={index} />
                <button
                  onClick={() => accept(project?._id)}
                  className="btn btn-success shadow-none mr-4"
                >
                  Accept
                </button>
                <button
                  onClick={() => refuse(project?._id)}
                  className="btn btn-danger shadow-none"
                >
                  Refuse
                </button>
              </div>
            ))}
          </React.Fragment>
        ))}
      </div>
      <div style={{ marginTop: "20px" }}>
        <button
          ref={loadMoreButtonRef}
          onClick={() => fetchNextPage()}
          disabled={!hasNextPage || isFetchingNextPage}
        >
          {isFetchingNextPage
            ? "Loading more ...."
            : hasNextPage
            ? "Load More"
            : "Nothing more to load"}
        </button>
      </div>
    </>
  );
};

export default ProjectValidation;
