import React from "react";
import "./ProjectDetailSide.css";
import parse from "html-react-parser";

const ProjectDetailSide = ({ project }) => {
  return (
    <>
      {project && (
        <div className="row">
          <div className="col-12 col-md-8">
            <p className="project-details text-justify">
              {parse(String(project?.description))}
            </p>
            <img
              className="footer-img"
              alt="profile"
              src={project.image_url}

            />
            <p className="project-details text-justify">
              {parse(String(project?.objectives))}
            </p>

          </div>
          <div className="col-12 col-md-3 ml-md-5 ">
            <div className="side-card">
              <h1 className="bold-title">Budget</h1>

              <h4 className="secondary-title">{project.total_cost}</h4>

              <h1 className="bold-title">Status</h1>

              <h4 className="secondary-title">{project?.status?.name}</h4>

              <h1 className="bold-title">Actual Start</h1>

              <h4 className="secondary-title">
                {project?.actual_start ? project?.actual_end?.split("T")[0] : ""}
              </h4>
              <h1 className="bold-title">Actual end</h1>
              <h4 className="secondary-title">
                {" "}
                {project?.actual_end ? project?.actual_end?.split("T")[0] : ""}
              </h4>
              <h1 className="bold-title">Country</h1>
              <h4 className="secondary-title">{project?.country?.name}</h4>
            </div>
          </div>
        </div>

      )}
    </>
  );
};

export default ProjectDetailSide;
