import Flag from "react-world-flags";
import React, { useState, useEffect, useCallback } from "react";
import { useHistory, useParams } from "react-router-dom";
import MTableProject from "../../components/aidchannel/webmaster/MTableProject";
import { getAllProjectsByCountry, deleteProject } from "../../apis/projectApi";
import TableProject from "components/aidchannel/webmaster/TableProject";

const ListProjects = () => {
  const { codeCountry } = useParams();
  const history = useHistory();
  const [projects, setprojects] = useState([]);
  const getDataFromApi = useCallback(async () => {
    const project = await getAllProjectsByCountry(codeCountry);
    setprojects(project?.reverse());
  }, [codeCountry]);
  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);
  const deletePjt = async (project) => {
    if (window.confirm(`Are you sure you wish to delete ${project.name} ?`)) {
      try {
        await deleteProject(project._id);
        const newProjectList = projects.filter(function(item) {
          if (item["_id"] === project?._id) return false;
          return true;
        });
        setprojects(newProjectList);
      } catch (error) {
        console.log(error);
      }
    }
  };

  return (
    <div className="container">
      <>
        <h1>
          <Flag
            code={codeCountry}
            height="70"
            width="70"
            style={{ marginRight: "2px" }}
            className="flag"
          />{" "}
          Projects
        </h1>
        <hr />
        <button
          onClick={() => history.push(`/web-master/add-project/${codeCountry}`)}
          className="btn btn-primary ml-3 my-3 shadow-none border-none"
        >
          Add
        </button>
        {/* <MTableProject data={projects} deleteRow={deletePjt} />*/}
        <TableProject />
      </>
    </div>
  );
};

export default ListProjects;
