import React, { useState, useEffect, useCallback } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useForm } from "react-hook-form";
import Select from "react-select";
import { getOneWebmaster, AddWebmaster } from "../../../apis/userApi";
import {
  getCountryByCode,
  getEnabledCountries,
} from "../../../apis/countryApi";
import "../../../components/aidchannel/webmaster/OrganizationForm";
// import AddWebmaster from "pages/superadmin/AddWebmaster";

const AddWebmasterForm = () => {
  const history = useHistory();
  const { codeCountry, idWebmaster } = useParams();
  const [content, setContent] = useState("");
  const [webmaster, setWebmaster] = useState();
  const [country, setCountry] = useState(null);

  const [selectedOption, setSelectedOption] = useState(null);
  const [optionscountry, setOptionscountry] = useState();

  const { register, handleSubmit, setValue } = useForm();

  const getDataFromApi = useCallback(async () => {
    if (idWebmaster !== undefined) {
      const webmaster = await getOneWebmaster(idWebmaster);
      setWebmaster(webmaster);
    }

    // }
    const country = await getEnabledCountries(codeCountry);
    setOptionscountry(
      country?.map((country) => {
        return {
          value: country._id,
          label: country.name,
        };
      })
    );
  }, [idWebmaster]);

  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);

  const onSubmit = async (data) => {
    let formData = new FormData();

    data.country = selectedOption?.value;

    await AddWebmaster(data);

    history.push(`/super-admin/webMaster/`);
  };

  const handleChangecountry = (selected) => {
    setSelectedOption(selected);
  };

  return (
    <div className="container">
      <form>
        <div className="row pb-5">
          <div className="centralize mt-4 col-12 ">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="fullname"
                spellCheck="false"
                {...register("fullname", {
                  required: "Name required",
                })}
              />
              <span className="placeholder">Full Name</span>
            </div>
          </div>

          <div className="col-12  centralize mt-4">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="email"
                spellCheck="false"
                {...register("email", {
                  required: "Email Required",
                  pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                })}
              />
              <span className="placeholder">Email</span>
            </div>
          </div>

          <div className="col-12  centralize mt-4">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="adress"
                spellCheck="false"
                {...register("password")}
              />
              <span className="placeholder">Password</span>
            </div>
          </div>
          <div className="col-12  centralize mt-4">
            <Select
              placeholder="Select the Country"
              value={selectedOption}
              onChange={handleChangecountry}
              options={optionscountry}
            />
          </div>
          <button
            onClick={handleSubmit(onSubmit)}
            type="submit"
            className="btn btn-primary w-100 h-25 submit-login my-5 "
          >
            Add Webmaster
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddWebmasterForm;
