import { getProject2 } from "apis/projectApi";
import React, { useState, useRef, useEffect, useCallback } from "react";
import { useParams, useHistory, Link } from "react-router-dom";
import Flag from "react-world-flags";
import AddProjectForm from "../../components/aidchannel/webmaster/ProjectForm";
import { acceptProject } from "../../apis/projectApi";
import ReactJson from "react-json-view";

import ListProjects from "./ListProjects";
import ListProjectsValides from "./ListProjectsValides";
import RawDataProject from "./RawDataProject";

const EditProject = () => {
  const [project, setProject] = useState();

  const { codeCountry, idProject, multimedia1 } = useParams();
  const history = useHistory();

  const getDataFromApi = useCallback(async () => {
    const project = await getProject2(idProject);
    setProject(project);
  }, [idProject]);
  const accept = async (projectId) => {
    await acceptProject(projectId);
    history.push(`/web-master/validationProjects/projectsNv/${codeCountry}`);
  };
  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);
  return (
    <div className="container">
      <h1>
        <Flag
          code={codeCountry}
          height="70"
          width="70"
          style={{ marginRight: "2px" }}
          className="flag"
        />{" "}
        Edit Project
      </h1>

      <ul className="nav nav-tabs">
        <li className="nav-item">
          <Link
            to={`/web-master/edit-project/${multimedia1}/${codeCountry}/${idProject}}`}
            className={`nav-link ${multimedia1 === "edit-project" && "active"}`}
          >
            Edit Project
          </Link>
        </li>
        <li className="nav-item">
          <Link
            to={`/web-master/edit-project/${multimedia1}/${codeCountry}/${idProject}}`}
            className={`nav-link ${multimedia1 === "raw-data" && "active"}`}
          >
            Raw Data
          </Link>
        </li>
      </ul>

      {multimedia1 === "edit-project" && <EditProject />}
      {multimedia1 === "raw-data" && <RawDataProject />}

      <AddProjectForm />

      {!project?.validation && (
        <button
          onClick={() => accept(project?._id)}
          className="btn btn-success w-100 h-25 my-2 shadow-none"
        >
          Accept Project
        </button>
      )}
    </div>
  );
};

export default EditProject;
