import React from "react";
import "./authentication.css";
import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";

const RegistrationForm = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    alert(JSON.stringify(data));
  };

  return (
    <form>
      <div className="text-center">
        <h1 className="login-title">Sign up</h1>
      </div>
      <div className="centralize mt-4">
        <div className="input-block">
          <input
            className="input"
            type="text"
            id="fullname"
            spellCheck="false"
            {...register("fullname", {
              required: "Full Name Required",
            })}
            required
          />
          <span className="placeholder">Full Name</span>
          {errors.fullname && errors.fullname.type === "required" && (
            <div className="text-danger mt-2">
              You must enter your full name
            </div>
          )}
        </div>
      </div>

      <div className="centralize mt-4">
        <div className="input-block">
          <input
            className="input"
            type="text"
            id="email"
            spellCheck="false"
            {...register("email", {
              required: "Email Required",
              pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
            })}
            required
          />
          <span className="placeholder">Email address</span>
          {errors.email && errors.email.type === "required" && (
            <div className="text-danger mt-2">You must enter your email</div>
          )}
          {errors.email && errors.email.type === "pattern" && (
            <div className="text-danger mt-2">You must enter a valid email</div>
          )}
        </div>
      </div>

      <div className="centralize mt-4">
        <div className="input-block">
          <input
            className="input"
            type="text"
            id="adress"
            spellCheck="false"
            {...register("adress", {
              required: "Address Required",
            })}
            required
          />
          <span className="placeholder">Address</span>
          {errors.adress && errors.adress.type === "required" && (
            <div className="text-danger mt-2">You must enter your address</div>
          )}
        </div>
      </div>

      <div className="centralize mt-4">
        <div className="input-block">
          <input
            className="input"
            type="text"
            id="telphone"
            spellCheck="false"
            {...register("telphone", {
              required: "Phone Number Required",
            })}
            required
          />
          <span className="placeholder">Phone Number</span>
          {errors.telphone && errors.telphone.type === "required" && (
            <div className="text-danger mt-2">
              You must enter your phone number
            </div>
          )}
        </div>
      </div>

      <div className="centralize mt-4">
        <div className="input-block">
          <input
            className="input"
            type="text"
            id="job_title"
            spellCheck="false"
            {...register("job_title", {
              required: "Job Title Required",
            })}
            required
          />
          <span className="placeholder">Job Title</span>
          {errors.job_title && errors.job_title.type === "required" && (
            <div className="text-danger mt-2">
              You must enter your job title
            </div>
          )}
        </div>
      </div>

      <div className="centralize mt-4">
        <div className="input-block">
          <input
            className="input"
            type="password"
            id="password"
            spellCheck="false"
            {...register("password", {
              required: "Password Required",
            })}
            required
          />
          <span className="placeholder">Password</span>
          {errors.password && errors.password.type === "required" && (
            <div className="text-danger mt-2">You must enter your password</div>
          )}
        </div>
      </div>

      <button
        onClick={handleSubmit(onSubmit)}
        type="submit"
        className="btn btn-primary form-control submit-login mt-4"
      >
        SIGN UP
      </button>
      <div className="col-12 text-center my-4">
        <h1 className="login-sub-title">
          Already have an account?
          <span className="login-sub-title-purple">
            {"  "}
            <Link
              className="text-decoration-none"
              style={{ color: "#3f51b5" }}
              to="/login"
            >
              {"    "}Sign in
            </Link>
          </span>
        </h1>
      </div>
    </form>
  );
};

export default RegistrationForm;
