import React from "react";
import MaterialTable from "material-table";
import { useHistory, useParams } from "react-router-dom";

import axios from "axios";
import urlImageAdapter from "helpers/urlImageAdapter";
import { deleteExpert } from "apis/userApi";

const TableExpert = () => {
  const orgTableRef = React.createRef();
  const history = useHistory();
  const { codeCountry } = useParams();

  const reloadTableData = () => {
    orgTableRef.current.onQueryChange();
  };

  return (
    <div>
      <MaterialTable
        tableRef={orgTableRef}
        title="List of experts"
        columns={[
          {
            field: "image_url",
            title: "Profil",
            render: (rowData) => (
              <img
                className="img-fluid"
                src={urlImageAdapter(rowData?.interviewImage)}
                style={{
                  verticalAlign: "middle",
                  width: "50px",
                  height: "50px",
                  borderRadius: "50%",
                }}
              />
            ),
            cellStyle: {
              width: "15%",
              textAlign: "center",
            },
          },
          {
            title: "Full Name",
            field: "fullname",
            cellStyle: {
              width: "25%",
              color: "#cb2b46",
              fontWeight: "bold",
              textAlign: "center",
            },
          },
          {
            title: "Email",
            field: "email",
            cellStyle: {
              width: "25%",
              color: "#cb2b46",
              fontWeight: "bold",
              textAlign: "center",
            },
          },
          {
            title: "Phone",
            field: "phone",
            cellStyle: {
              width: "25%",
              fontWeight: "bold",
              textAlign: "center",
            },
          },
        ]}
        data={(query) =>
          new Promise((resolve, reject) => {
            let url = `${process.env.REACT_APP_BACKEND}user/byCodeCountryPagination/${codeCountry}?`;
            url += "limit=" + query.pageSize;
            url += "&page=" + (query.page + 1);
            axios
              .post(url, {
                searchText: query.search,
              })
              .then((result) => {
                resolve({
                  data: result.data.data,
                  page: result.data.page - 1,
                  totalCount: result.data.totalCount,
                });
              });
          })
        }
        options={{
          filtering: false,
          search: true,
          sorting: false,
          headerStyle: {
            backgroundColor: "#2acaea",
            textAlign: "center",
          },
          actionsColumnIndex: -1,
        }}
        actions={[
          {
            icon: "refresh",
            tooltip: "Refresh Data",
            isFreeAction: true,
            onClick: () =>
              orgTableRef.current && orgTableRef.current.onQueryChange(),
          },
          /*  {

            icon: (event, user) => (
              <button className="btn btn-primary border-none shadow-none">
                Expert of the Month
              </button>
            ),
            onClick: (event, interview) => {
              history.push(
                `/web-master/details-interview/${codeCountry}/${interview?._id}`
              );
            },
          },*/
          {
            icon: () => (
              <button className="btn btn-danger border-none shadow-none">
                Delete
              </button>
            ),
            onClick: async (event, user) => {
              if (
                window.confirm(
                  `Are you sure you wish to delete ${user.fullname} ?`
                )
              ) {
                try {
                  await deleteExpert(user?._id);
                  reloadTableData();
                  reloadTableData();
                } catch (error) {
                  console.log(error);
                }
              }
            },
          },
        ]}
      />
    </div>
  );
};

export default TableExpert;
