import React from "react";
import "./Sidebar.css";
import { Avatar } from "@material-ui/core";
//import { useSelector } from "react-redux";
//import { selectUser } from "./features/userSlice";

function Sidebar() {
  //const user = useSelector(selectUser);
  const recentItem = (topic) => (
    <div className="sidebar__recentItem">
      <span className="sidebar__hash">#</span>
      <p>{topic}</p>
    </div>
  );

  return (
    <div className="sidebar">
      <div className="sidebar__top">
        <img
          src="https://audreytips.com/wp-content/uploads/2017/08/comment-trouver-des-clients-avec-linkedin-11.jpg"
          alt="background-banner"
        />
        <Avatar
          src={
            "https://media-exp1.licdn.com/dms/image/C4D03AQGDiWuWWd12MA/profile-displayphoto-shrink_200_200/0/1617837846595?e=1635379200&v=beta&t=mrhHf9x3j2LoDQEwjJeDs0wzl7iOqeVWioy-vbzCxPg"
          }
          className="sidebar__avatar"
        ></Avatar>
        <h2>{"Oussama Basry"}</h2>
        <h4>{"Full Stack web developper"}</h4>
      </div>
      <div className="sidebar__stats">
        <div className="sidebar__stat">
          <p>Who viewed you</p>
          <p className="sidebar__statNumber">1547</p>
        </div>
        <div className="sidebar__stat">
          <p>Views on post</p>
          <p className="sidebar__statNumber">300</p>
        </div>
      </div>
      <div className="sidebar__bottom">
        <p>Recent</p>
        {recentItem("USAID")}
        {recentItem("ZIG")}
        {recentItem("who")}
        {recentItem("WORLD BANK")}
      </div>
    </div>
  );
}

export default Sidebar;
