import React, { useState, useRef, useEffect, useCallback } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useForm } from "react-hook-form";
import JoditEditor from "jodit-react";
import { AiFillFileAdd } from "react-icons/ai";
import Select from "react-select";
import { getCountryByCode } from "../../../apis/countryApi";
import {
  AddProject,
  getAllHeadProjectsWithIdAndName,
  getProject2,
  updateProject,
} from "../../../apis/projectApi";
import "./OrganizationForm.css";
import { getThematiques } from "apis/thematiqueApi";
import { getStatus } from "apis/statusApi";
import urlImageAdapter from "helpers/urlImageAdapter";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
// import Form from "react-bootstrap/Form";
import {
  Form,
  Row,
  Button,
  FormGroup,
  FormControl,
  FormLabel,
  Col,
} from "react-bootstrap";

const AddProjectForm = () => {
  const history = useHistory();
  const { codeCountry, idProject } = useParams();
  const editor = useRef(null);
  const [content, setContent] = useState("");
  const [thematiquesOptions, setThematiquesOptions] = useState();
  const [statusOptions, setStatusOptions] = useState();
  const [selectedOption, setSelectedOption] = useState(null);
  const [selectedStatus, setSelectedStatus] = useState(null);
  const [status, setStatus] = useState(null);
  const [project, setProject] = useState();
  const [country, setCountry] = useState();
  const [projectLogoPreview, setProjectLogoPreview] = useState(null);
  const [projectLogo, setProjectLogo] = useState();
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [actualDate, setActualDate] = useState(new Date());
  const [plannedEnd, setPlannedEnd] = useState(new Date());
  const [actualEnd, setActualEnd] = useState(new Date());
  const config = {
    readonly: false,
    placeholder: "Description of the project",
  };
  const configObj = {
    readonly: false,
    placeholder: "Ojectives of the project",
  };

  const { register, handleSubmit, setValue } = useForm();

  const getDataFromApi = useCallback(async () => {
    const thematiques = await getThematiques();
    setThematiquesOptions(
      thematiques?.map((thematique) => {
        return {
          value: thematique._id,
          label: thematique.name,
        };
      })
    );

    const status = await getStatus();
    setStatus(status);
    setStatusOptions(
      status?.map((sta) => {
        return {
          value: sta?._id,
          label: sta?.name,
        };
      })
    );
    const country = await getCountryByCode(codeCountry);
    setCountry(country);

    if (idProject !== undefined) {
      const project = await getProject2(idProject);
      setProject(project);
      setProjectLogoPreview(urlImageAdapter(project?.image_url));
      setSelectedOption({
        value: project?.thematique?._id,
        label: project?.thematique?.name,
      });
      setSelectedStatus({
        value: project?.status?._id,
        label: project?.status?.name,
      });

      setContent(project?.description);
      setContent(project?.objectives);
      setContent(project?.approval_date);
      setContent(project?.actual_start);
      setContent(project?.actual_end);
      setContent(project?.planned_end);
      // setContent(project?.objectives);
      setValue("name", project?.name);
      setValue("total_cost", project?.total_cost);
      setValue("budget", project?.budget);
      setValue("image_url", project?.image_url);
      setValue("approval_date", project?.approval_date);
      setValue("actual_start", project?.actual_start);
      setValue("actual_end", project?.actual_end);
      setValue("planned_end", project?.planned_end);
    }
  }, [idProject, setValue]);

  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);

  const onSubmit = async (data) => {
    //const project = await getProject2(selectedOption?.value);
    let formData = new FormData();
    formData.append("namefr", "");
    formData.append("country", country?._id);
    formData.append("thematique", selectedOption?.value);
    formData.append("status", selectedStatus?.value);

    if (data.total_cost === "") formData.append("total_cost", null);
    else formData.append("total_cost", data.total_cost);
    if (data.budget === "") formData.append("budget", null);
    else formData.append("budget", data.budget);
    if (data.approval_date === "") formData.append("approval_date", null);
    else formData.append("approval_date", data.approval_date);
    if (data.actual_start === "") formData.append("actual_start", null);
    else formData.append("actual_start", data.actual_start);
    if (data.actual_end === "") formData.append("actual_end", null);
    else formData.append("actual_end", data.actual_end);
    if (data.planned_end === "") formData.append("planned_end", null);
    else formData.append("planned_end", data.planned_end);
    formData.append("name", data.name);
    if (content === "") formData.append("description", null);
    else formData.append("description", content);
    if (content === "") formData.append("objectives", null);
    else formData.append("objectives", content);
    if (projectLogo != undefined) formData.append("projectImage", projectLogo);

    // data.namefr = "";
    data.country = country?._id;
    data.thematique = selectedOption?.value;
    data.status = selectedStatus?.value;

    if (data.total_cost === "") data.total_cost = null;
    if (data.budget === "") data.budget = null;

    if (data.actual_start === "") data.actual_start = null;
    if (data.actual_end === "") data.actual_end = null;
    if (data.approval_date === "") data.approval_date = null;
    if (data.planned_end === "") data.planned_end = null;

    if (content === "") data.description = null;
    else data.description = content;
    if (content === "") data.objectives = null;
    else data.objectives = content;

    //data.image_url = project?.image_url;

    if (idProject !== undefined) {
      await updateProject(idProject, formData);
    } else {
      await AddProject(formData);
    }

    history.push(`/web-master/projects/${codeCountry}`);
  };

  const handleThematiqueChange = (selected) => {
    setSelectedOption(selected);
  };

  const handleChangeStatus = (selected) => {
    setSelectedStatus(selected);
  };
  const imagePreview = (e) => {
    setProjectLogoPreview(URL.createObjectURL(e.target.files[0]));
    setProjectLogo(e.target.files[0]);
  };
  return (
    <div className="container">
      <form>
        <div className="row pb-5">
          <div className="centralize mt-4 col-12 col-lg-6">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="name"
                spellCheck="false"
                {...register("name", {
                  required: "Name required",
                })}
              />
              <span className="placeholder">Name</span>
            </div>
          </div>
          <div className="form-group m-auto">
            <label className="ml-4 logo-file d-inline" htmlFor="projectLogo">
              {projectLogoPreview && (
                <img
                  src={projectLogoPreview}
                  alt="avatar"
                  style={{ cursor: "pointer" }}
                  width="15%"
                  height="15%"
                  className="avatar"
                />
              )}
              {!projectLogoPreview && (
                <span style={{ cursor: "pointer" }}>
                  <AiFillFileAdd color="blue" size={40} />
                </span>
              )}
            </label>
            <input
              type="file"
              className="form-control-file ml-4 shadow-none logo-file d-none"
              id="projectLogo"
              name="projectLogo"
              onChange={imagePreview}
              multiple={false}
            />
          </div>
          <div className="col-12 col-lg-6 centralize mt-5">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="total_cost"
                spellCheck="false"
                {...register("total_cost")}
              />
              <span className="placeholder">Total Cost</span>
            </div>
          </div>
          <div className="col-12 col-lg-6 centralize mt-5">
            <div className="input-block">
              <input
                className="input-org-form"
                type="text"
                id="budget"
                spellCheck="false"
                {...register("budget")}
              />
              <span className="placeholder">Task Manager</span>
            </div>
          </div>
          <div className="col-12 col-lg-4 centralize mt-5">
            <Select
              placeholder="Select the thematic "
              value={selectedOption}
              onChange={handleThematiqueChange}
              options={thematiquesOptions}
            />
          </div>{" "}
          <div className="col-12 col-lg-4 centralize mt-5">
            <Select
              placeholder="Select the funder "
              value={selectedOption}
              onChange={handleThematiqueChange}
              options={thematiquesOptions}
            />
          </div>{" "}
          <div className="col-12 col-lg-4  centralize mt-5">
            {" "}
            <Select
              placeholder="Select the Status "
              value={selectedStatus}
              onChange={handleChangeStatus}
              options={statusOptions}
            />
          </div>
          <Row className="col-12 centralize mt-4">
            <Col sm={3}>
              <FormLabel>Approval Date</FormLabel>
              <FormGroup>
                <DatePicker
                  className="input-org-form"
                  value={selectedOption}
                  selected={approvalDate}
                  onChange={(date) => setApprovalDate(date)}
                />
              </FormGroup>
            </Col>
            <Col sm={3}>
              <FormLabel>Actual Start</FormLabel>
              <DatePicker
                className="input-org-form"
                selected={actualDate}
                onChange={(date) => setActualDate(date)}
              />
            </Col>
            <Col sm={3}>
              <FormLabel>Actual End</FormLabel>
              <DatePicker
                className="input-org-form"
                selected={actualEnd}
                onChange={(date) => setActualEnd(date)}
              />
            </Col>{" "}
            <Col sm={3}>
              <FormLabel>Planned End</FormLabel>
              <DatePicker
                className="input-org-form"
                placeholder="Placeholder"
                selected={plannedEnd}
                onChange={(date) => setPlannedEnd(date)}
              />
            </Col>
          </Row>
          <div className="col-12 mt-4">
            <JoditEditor
              ref={editor}
              value={content}
              config={config}
              tabIndex={1}
              onBlur={(newContent) => setContent(newContent)}
            />
          </div>{" "}
          <div className="col-12 mt-4">
            <JoditEditor
              ref={editor}
              value={content}
              config={configObj}
              tabIndex={1}
              onBlur={(newContent) => setContent(newContent)}
            />
          </div>{" "}
          <button
            onClick={handleSubmit(onSubmit)}
            type="submit"
            className="btn btn-primary w-100 h-25 submit-login my-5 "
          >
            {idProject !== undefined ? "Edit Project" : "Add Project"}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddProjectForm;
