import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import { getExpert } from "../../apis/userApi";
import PublicFooter from "../../components/layout/footer/PublicFooter";
import ExpertDetailHeader from "../../components/aidchannel/expert/ExpertDetailHeader";
import CountryHeader from "../../components/layout/header/CountryHeader";
import ExpertDetailBody from "../../components/aidchannel/expert/ExpertDetailBody";
import ExpertContact from "../../components/aidchannel/expert/ExpertContact";
import PublicHeader from "components/layout/header/PublicHeader";

const LearnMoreExpert = () => {
  const [expert, setExperts] = useState();
  const { expert_id } = useParams();

  const getDataFromApi = useCallback(async () => {
    const expert = await getExpert(expert_id);
    console.log(expert);
    setExperts(expert);
  }, [expert_id]);

  useEffect(() => {
    getDataFromApi();
  }, [getDataFromApi]);

  return (
    <div>
      <PublicHeader />
      {expert && (
        <div className="row">
          <div className="col-12 ">
            <ExpertDetailBody expert={expert} />
          </div>
        </div>
      )}
      <PublicFooter />
    </div>
  );
};

export default LearnMoreExpert;
