import "./authentication.css";
import "./authentication.css";
import toastr from "toastr";
import { Link } from "react-router-dom";
import React, { useState } from "react";
import "toastr/build/toastr.css";
import { useHistory } from "react-router-dom";

const LoginForm = () => {
   const history = useHistory();
  const [user, setUser] = useState({
    email: "",

    password: "",
  });

  const handleChange = (e) => {
    setUser({ ...user, [e.target.id]: e.target.value });
  };

  const submitSignIn = (e) => {
    e.preventDefault();
    fetch("http://localhost:3010/auth/signin", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user),
    })
      .then((res) => res.json())
      .then((res) => {
        if (res.error) {
          toastr.warning(res.error, "Please check your informations!", {
            positionClass: "toast-bottom-left",
          });
        } else {
          toastr.info( "Welcome among us ", {
            positionClass: "toast-bottom-left",
          });
          history.push('/');
        }
      })

      .catch((err) => console.log(err));
  };

  return (
    <form onSubmit={submitSignIn}>
      <div className="centralize">
        <div className="input-block">
          <input
            onChange={handleChange}
            className="input"
            type="text"
            id="email"
            spellCheck="false"
          />
          <span className="placeholder">Email address</span>
        </div>
      </div>
      <div className="centralize mt-4">
        <div className="input-block">
          <input
            onChange={handleChange}
            className="input"
            type="password"
            id="password"
            spellCheck="false"
            required
          />
          <span className="placeholder">Password</span>
        </div>
      </div>

      <button
        type="submit"
        className="btn btn-primary form-control submit-login mt-4"
      >
        SIGN IN
      </button>
    </form>
  );
};

export default LoginForm;
