import React, { useState, useEffect } from "react";
import { getProjectsOfMonthApi } from "../../apis/projectApi";

import CardInfoDetails from "../../components/aidchannel/general/CardInfoDetails";

import CountryHeader from "../../components/layout/header/CountryHeader";
import PublicFooter from "../../components/layout/footer/PublicFooter";
import CardSmall from "../../components/aidchannel/general/CardSmall";

const HomeCountry = (props) => {
  const [project, setProject] = useState([]);

  useEffect(() => {
    getDataFromApi();
  }, []);

  const getDataFromApi = async () => {
    const proj = await getProjectsOfMonthApi(1, 1);
    setProject(proj.data[0]);
  };

  const cop = {
    name: "Mickeal Jean",
    image_url:
      "https://assets.maccarianagency.com/the-front/photos/coworking/place3.jpg",
  };
  const donor = {
    name: "Makasa Higen",
    image_url:
      "https://share.america.gov/wp-content/uploads/2021/03/USAID-Women-Leaders-Climate-Crisis-49534646426_44e4a8da7c_o.jpg",
  };
  const beneficiary = {
    name: "Thierly Oberlen",
    image_url: "https://miro.medium.com/max/1400/1*q_sc5BCE-VIHxnX4teOrvw.jpeg",
  };
  const overview = {
    name: "Thierly Oberlen",
    image_url: "/assets/overview.png",
  };

  return (
    <>
      <CountryHeader />
      <div className="container">
        <h1 className="heading_background  mt-5 mb-5 pb-3 pt-3">
          Project<span className="sub-heading"> of the month</span>
        </h1>
        <CardInfoDetails data={project} />

        <div class="row">
          <div class="col-12 col-md-3">
            <h1 className="heading_background  mt-3 mb-4 pb-3 pt-1">
              COP<span className="sub-heading"> Interview</span>
            </h1>
            <CardSmall data={cop} />
          </div>

          <div className="col-12 col-md-3">
            <h1 className="heading_background  mt-3 mb-4 pb-3 pt-1">
              Donor<span className="sub-heading"> Interview</span>
            </h1>
            <CardSmall data={donor} />
          </div>

          <div className="col-12 col-md-3">
            <h1 className="heading_background  mt-3 mb-4 pb-3 pt-1">
              Beneficiary<span className="sub-heading"> Interview</span>
            </h1>
            <CardSmall data={beneficiary} />
          </div>

          <div className="col-12 col-md-3">
            <h1 className="heading_background  mt-3 mb-4 pb-3 pt-1">
              Project<span className="sub-heading"> Overview</span>
            </h1>
            <CardSmall data={overview} />
          </div>
        </div>
      </div>

      <PublicFooter />
    </>
  );
};

export default HomeCountry;
